import React, { useEffect, useState } from 'react'
import "./flowerlist.css"
import { Link } from 'react-router-dom'

// const url = "https://flowers-mock-data.firebaseio.com"

export const FlowerList = () => {
  const [flowers, setFlowers] = useState([])

useEffect(() => {
  // fetch(url)
  // fetch('https://api.themoviedb.org/3/movie/popular?api_key=3812b9925d12c2723ac148f3607b8bb5&language=en-US&page=1')
  fetch('https://flowers-mock-data.firebaseio.com')
    .then((res) => res.json())
    .then((json) => {
      setFlowers(json.results)
    })
}, [])

// function goBack() {
//     window.history.back();
//   }

  return (

    <div className="flowerContainer">
      {flowers.map((flower) => (
        <div className="flowerPoster" key={flower._id}>
          <Link to={`/flowers/${flower._id}`}>
            <div className="titleRelease">
              <h1>{flower.common_name}</h1>
              <p>Released {flower.release_date}</p>
            </div>
            <div>
              <img src={`https://image.tmdb.org/t/p/w342${flower.poster_path}`} alt={flower.original_title} />  
            </div>
          </Link>
        </div>
      ))}  
    </div>
  )
}
